import pandas as pd
from fillpdf import fillpdfs

def read_csv():
    df = pd.read_csv("7a.csv")
    result = df[df['الصف و الشعبة']=='الصف السادس-د'].to_string(index=False) 
    names = df['اسم الطالب'].tolist()
    return names
names =read_csv()


''' 
تفاصيل الخانات في صفحة الاحوال
40 - 80 الارقام
80 - 120 الاسم الاول
120 - 160 الاسم الثاني
160 - 200 الاسم الثالث 
200 - 240 الاسم الرابع
240 - 280 يوم ميلاد الاسم
280 - 320 شهر ميلاد الاسم
320 - 360 سنة ميلاد الاسم
360 - 400 مكان الولادة
400 - 440 الجنسية
440 - 480 الديانه
480 - 520 يوم العمر في بداية العام
520 - 560 شهر العمر في بداية العام
560 - 600 سنة العمر في بداية العام
600 - 640 اسم ولي الامر (الاب)
640 - 680  درجة القرابة
680 - 720 عمله
720 - 760 رقم الهاتف
760 - 800 السكن

1-40 الارقام
name1-40 الاسماء

'''
# try:
counter = 1
for name in names :

    n =name.split(' ')
    fields[f'untitled{counter}'] = counter
    fields[f'untitled{counter+40}'] = n[0]
    fields[f'untitled{counter+80}'] = n[1]
    fields[f'untitled{counter+120}'] = n[2]
    fields[f'untitled{counter+160}'] = n[3]
    fields[f'untitled{counter+360}'] = 'اردني'
    fields[f'untitled{counter+400}'] = "الاسلام"
    fields[f'untitled{counter+560}'] = n[1]
    fields[f'untitled{counter+600}'] = "اب"

    fields[str(counter)] = f'{str(counter) :>2}'
    fields[f'name{counter}'] = f'{str(name):>40}'
    fields['year1'] ='2'
    fields['year2'] ='3'
    fields['directory'] ='لواء الاغوار الجنوبي'
    fields['xchool'] ="مدرسة المزرعة الثانوية للبنين"
    fields['className'] ='السابع'
    fields['gradename'] = f"{'(أ)'}"
    fields['teacherName']= f"{'انــــس الجعافرة' :<40}"
    
    counter+=1

